    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (307, 2, "Zkoušky souborů", "zkousky-souboru", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/bd5e3beac423ccd3b89f7c6bdde58dd3.jpg\" alt=\"\" />Školní rok se chýlí ke konci a s ním i veřejná vystoupení. V následujícím týdnu se jedná o tyto termíny: Mladší flétny úterý 13.00 hod., starší flétny středa 16.45 hod. a klarinety ve čtvrtek v 16.45 hod.! Účast všech nutná!", "", "", 1308143477, "12.5.2007");');
