    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (319, 2, "Vítejte v novém školním roce!", "vitejte-v-novem-skolnim-roce!", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/7289f772898ad756c1c2795d2bcf829c.jpg\" alt=\"\" />Tak Vás opět vítáme v naší zušce! Ohlaste se co nejdříve u svých učitelů a dohodněte si rozvrh! I pro nové zájemce jsou učitelé k dispozici vždy v odpoledních hodinách. Noví i stávající výtvarníci přijdou v úterý od 13 hodin! Neotálejte!!", "", "", 1308143477, "4.9.2007");');
    $db->execute('insert into `article_attached_label`(`article_id`, `label_id`) values(319, 3);');
