    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (326, 2, "Flétnový soubor", "fletnovy-soubor", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/ea42a8876551d39446f2d4397ec2a41b.jpg\" alt=\"\" />Soubor mladých flétnistů opět pokračuje ve své činnosti ve středu 31.10. od 13 hodin a poté každý týden ve stejnou dobu!", "", "", 1308143477, "29.10.2007");');
