    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (346, 2, "Přihlášky na 9. Víkendové muzicírování", "prihlasky-na-9.-vikendove-muzicirovani", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/a7fb8211fd45c57435ed70e2b1206e42.jpg\" alt=\"\" />Kdo z mladých hudebníků má zájem o účast na již 9. ročníku Víkendového muzicírování, které letos proběhne 9. až 12.10. v Kytlici, nechť se přihlásí u pana uč. P. Donefa, nebo vyplní přihlášku, kterou najde na \"úřední desce\" a řídí se jejími pokyny!", "", "", 1308143477, "12.4.2008");');
