    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (348, 2, "Poslední týden!", "posledni-tyden!", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/bd7660a05a57bce903aeb194d28972f1.jpg\" alt=\"\" />Pro zájemce o 9. ročník Víkendového muzicírování je v tomto týdnu poslední možnost podání přihlášky! Tedy neváhejte a zamuzicírujte si v říjnu s kamarády! Přihlášku najdete na \"úřední desce\"...", "", "", 1308143477, "19.5.2008");');
    $db->execute('insert into `article_attached_label`(`article_id`, `label_id`) values(348, 3);');
