    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (353, 2, "Závěrečný koncert je minulostí", "zaverecny-koncert-je-minulosti", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/0dbe179286374fe36b628f4342210b50.jpg\" alt=\"\" />Bez přestávky celé dvě hodiny vydrželi návštěvníci Závěrečného konceru ZUŠ sledovat výkony našich mladých hudebníků. Jako vždy se střídavými úspěchy přednesli své skladby, nicméně snaha ukázat to nejlepší byla patrná u všech. Zpestřením závěru koncertu bylo krátké vystoupení skupiny No Headache, kterou tvoří chlapci od 12 do 14 let <br />Součástí byla i ukázka prací žáků výtvarného oboru na hlavním sále. <br />Všem zúčastněným děkujeme!", "", "", 1308143477, "20.6.2008");');
    $db->execute('insert into `article_attached_label`(`article_id`, `label_id`) values(353, 3);');
