    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (387, 2, "Výlet výtvarníků zrušen!", "vylet-vytvarniku-zrusen!", "", "Výlet žáků výtvarného oboru, který se měl konat v úterý 26.5. je zrušen! Důvodem je společné fotografování tříd na ZŠ. Odpoledne bude tedy probíhat normální výuka výtvarky. Další podrobnosti sdělí paní uč. L.Jelínková.", "", "", 1308143477, "22.5.2009");');
