    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (389, 2, "Výlet výtvarníků 23.6.!", "vylet-vytvarniku-23.6.!", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/0b817c7d379c05a616e5f07ae6f374b3.jpg\" alt=\"\" />Výlet výtvarníků, který byl zrušen kvůli fotografování tříd v ZŠ, se uskuteční v náhradním termínu, v úterý 23.6. ! Podrobnosti vám sdělí pí. uč. Jelínková.<br />", "", "", 1308143477, "26.5.2009");');
