    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (394, 2, "Rozloučení se školním rokem", "rozlouceni-se-skolnim-rokem", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/20faf130035ca64e12db8bd78794f871.jpg\" alt=\"\" />Ve středu 24.6. dopoledne zahráli naši hudebníci svým kamarádům ze základní školy. Nejdříve prvnímu stupni a poté starším žákům. Přesto, že počasí zrovna nepřálo, nálada byla výborná, zvlášť při produkci skupiny No Headache, o čemž svědčí i fotografie v galerii.<br />Druhý den ve čtvrtek převzali naši žáci vysvědčení, dopřáli si malého občerstvení a někteří si i zazpívali. Tak s dobrou náladou hurá na prázdniny!!!", "", "", 1308143477, "25.6.2009");');
    $db->execute('insert into `article_attached_label`(`article_id`, `label_id`) values(394, 3);');
