    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (405, 2, "Program interního koncertu", "program-interniho-koncertu", "", "Program interního koncertu 1.12.09 najdete na \"úřední desce\"! Slouží vnitřním potřebám ZUŠ ke skladbě programu veřejného koncertu.", "", "", 1308143477, "29.11.2009");');
