    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (406, 2, "Adventní koncert", "adventni-koncert", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/b337f409f1eff927615610015a69e624.jpg\" alt=\"\" />Najděte si ve čtvrtek 10.12. od 17. hodiny čas a přijďte si do Tří lip užít atmosféry Adventního koncertu žáků ZUŠ! Vedle hudebníků Vám svoji tvorbu představí i žáci výtvarného oboru, pod vedením pí uč. R.Hoškové<br />Těšíme se na Vás", "", "", 1308143477, "4.12.2009");');
