    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (412, 2, "No Headache do Litvínova", "no-headache-do-litvinova", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/81a22ff85670a631ed797d04beeb9932.jpg\" alt=\"\" />Ve čtvrtek 25.3. se naše skupina No Headache, doplněná o Kamilu Svobodovou-sax, Lad. Markvarta, Romana Horkého-klarinety a Jana Zábranského-klávesy, zúčastní krajského kola soutěže ZUŠ, vyhlášenou MŠMT ČR. Soutěž proběhne v litvínovské Citadele, která je součástí místní ZUŠ. Pod vedením p. uč. Černého naši hudebníci připravili pro soutěžní klání čtyři skladby, které ještě ten samý den od 17 hodin představí také na Jarním koncertu v KS U Tří lip. Zveme všechny příznivce!<br />Odjezd na soutěž bude v 7 hodin od budovy ZUŠ v Libochovicích! Držíme palce!", "", "", 1308143477, "17.3.2010");');
    $db->execute('insert into `article_attached_label`(`article_id`, `label_id`) values(412, 3);');
