    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (413, 2, "Navštivte Jarní koncert!", "navstivte-jarni-koncert!", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/e425382335430d35185245b5494b3cf8.jpg\" alt=\"\" />Ve čtvrtek 25.3.2010 přijďte od 17 hodin povzbudit naše mladé umělce při jejich účinkování na tradičním Jarním koncertu v sále KS U Tří lip. Po náročném soutěžním klání se představí i skupina No Headache se svým soutěžní programem! Program najdete na Úřední desce. Těšíme se na Vás!", "", "", 1308143477, "22.3.2010");');
    $db->execute('insert into `article_attached_label`(`article_id`, `label_id`) values(413, 3);');
    $db->execute('insert into `article`(`id`, `line_id`, `order`, `visible`) values (414, 17, 414, 2);');
    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (414, 2, "Zkouška ohněm!", "zkouska-ohnem!", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/f23fa54fd07596f0d88653960e16f961.jpg\" alt=\"\" />Naše skupina No Headache se v soutěžním klání jedenácti souborů kraje, které se konalo 25.3. v Litvínově, rozhodně neztratila! Naši hudebníci dokázali, že si mohou zahrát i v konkurenci těch nejlepších. Vždyť zušky s několikanásobným počtem žáků (Ústí n.L., Most, Děčín...) tuto soutěž neobsadily. Ocenění 2. místem je tak pro náš soubor výborným povzbuzením do další práce!<br />Porotu rozhodně zaujali, o čemž svědčí i fakt, že mezi třemi oceněnými interprety, kteří zaujali svým výkonem, byl jediný instrumentalista - hráč na bicí nástroje Patrik Suchý z No Headache (další dvě ocenění získaly jazzové zpěvačky z Jirkova a Litvínova).<br />Děkujeme tak našim hudebníkům a přejeme hodně dalších úspěchů a zájemců o jejich vystoupení!", "", "", 1308143477, "29.3.2010");');
    $db->execute('insert into `article_attached_label`(`article_id`, `label_id`) values(414, 3);');
