    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (415, 2, "Jarní koncert", "jarni-koncert", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/a8478745c900d0e81c739217fd1fe693.jpg\" alt=\"\" />Vskutku bouřlivým potleskem odměnil na závěr koncertu zaplněný sál Kulturního střediska U Tří lip mladé hudební interprety! A zaslouženě! Po celodenní soutěži se skupina No Headache představila na domácí scéně v plné parádě. Děkujeme a těšíme se na koncert závěrečný ve čtvrtek 17.června!<br />Viz fotogalerie", "", "", 1308143477, "31.3.2010");');
