    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (417, 2, "Výstava Lidice 2010", "vystava-lidice-2010", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/90bdcb3274d3c0478f64c051cbdad305.jpg\" alt=\"\" />Výtvarné práce, s nimiž se naši žáci aktivně zůčastnili 38. ročníku Mezinárodní dětské výstavy Lidice 2010, najdete ve fotogalerii. Výsledky výstavy se dozvíme koncem května. Držíme palce!", "", "", 1308143477, "6.4.2010");');
