    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (420, 2, "No Headache na Zahradě Čech", "no-headache-na-zahrade-cech", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/5f3f9b40d3ca93e5063c9e367ae4bdef.jpg\" alt=\"\" />Libochovická hudební skupina ZUŠ No Headache vystoupí v neděli 16.5. v rámci výstavy Tempo 2010 na litoměřickém výstavišti Zahrada Čech. Od 10 hodin zahájí přehlídku mládežnických kapel základních uměleckých škol. Naváhejte a přijďte do Litoměřic!<br />Kulturní program viz úřední deska.", "", "", 1308143477, "14.5.2010");');
    $db->execute('insert into `article_attached_label`(`article_id`, `label_id`) values(420, 3);');
