    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (426, 2, "Vysvědčení", "vysvedceni", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/489e65ae40f68464cf15dd7553be49f9.jpg\" alt=\"\" />Předávání vysvědčení za uplynulý školní rok, tradičně spojený s malým občerstvením, se uskuteční ve středu 30.6. od 10.hodiny!", "", "", 1308143477, "22.6.2010");');
