    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (431, 2, "Hudební nauka", "hudebni-nauka", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/8ebd0210c70d004d446f0024f01d6855.jpg\" alt=\"\" />Výuka hudební nauky začíná ve středu 15.září od 13. hodiny! První skupinu povede pí uč. L.Abrahámová, druhou a třetí p. uč. P. Donef. Více o složení skupin se dočtete na Úřední desce", "", "", 1308143477, "16.9.2010");');
