    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (437, 2, "Poděkování", "podekovani", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/ef0a05c62fc2d6e3f4f75aee4acd05e7.jpg\" alt=\"\" />Touto cestou děkujeme panu Jar. Polívkovi za poskytnutí pěkných fotografií z Vánočního koncertu, které můžete shlédnout ve fotogalerii a panu St. Tlustému za pořízení videozáznamu. Zájemci o foto či DVD se mohou přihlásit v ZUŠ.", "", "", 1308143477, "17.12.2010");');
    $db->execute('insert into `article_attached_label`(`article_id`, `label_id`) values(437, 3);');
