    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (439, 2, "Úspěšný rok 2011!", "uspesny-rok-2011!", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/73adb7193f389d3275cf145e96ae0a9a.jpg\" alt=\"\" />Všem příznivcům našich stránek přejeme úspěšný rok 2011! Nechť Vám zdraví slouží k životní pohodě! Našim žákům přejeme hodně radosti ze své umělecké činnosti!<br />Kolektiv zaměstnanců ZUŠ Libochovice.", "", "", 1308143477, "31.12.2010");');
