    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (441, 2, "Omluva", "omluva", "", "Omlouváme se některým bezhotovostním plátcům školného za neuskutečnění inkasní platby za druhé pololetí. Po nezbytných úpravách dojde k transakci koncem měsíce února. K tomuto nedopatření z naší strany dochází maximálně jednou za dvacet let ;-)... Děkujeme za pochopení!", "", "", 1308143477, "16.2.2011");');
