    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (451, 2, "Poděkování", "podekovani", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/91b8a67a54bd37d8ceb7920969e4bdeb.jpg\" alt=\"\" />Vedle tradičních koncertů vystupují také jednotliví žáci naší školy na různých akcích, většinou slavnostních, aby je obohatili o kulturní složku. Tak se také nedávno stalo 7.května u příležitosti vzpomínky na konec 2.svět. války na libochovickém hřbitově, kde účinkovali Jan Pavlata a Aneta Hodinová na klarinety. Zahájení výstavy fotografií ve výstavní síni zámku 13.května kytarovým vystoupením zpestřili Michal Havlíček s Tomášem Kubíčkem. Všem upřímně děkujeme!", "", "", 1308143477, "29.5.2011");');
    $db->execute('insert into `article_attached_label`(`article_id`, `label_id`) values(451, 3);');
