    $db->execute('insert into `article_content`(`article_id`, `language_id`, `name`, `url`, `keywords`, `head`, `content`, `author`, `timestamp`, `datetime`) values (467, 2, "Navštivte naši fotogalerii!", "navstivte-nasi-fotogalerii!", "", "<img src=\"http://www.skolylibochovice.cz/zus/aktualne/images/5a3ec2b778f22b0e296ff4cee2665e62.jpg\" alt=\"\" />Ve fotogalerii Vánoční koncert 2011 najdete nové fotografie z koncertu pro ZŠ!", "", "", 1308143477, "27.12.2011");');
